$('a[href*=#]:not([href=#])').click(function() {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
        if (target.length) {
            $('html,body').animate({
                scrollTop: target.offset().top
            }, 600);
            return false;
        }
    }
});

$(document).on("click","#burger",function() {
    if( $('#burger > i').hasClass('fa-bars') ) {
        $('#header').css({ 'right': '0' });
        $('#main').css({ 'transform': 'translateX(-16em)' });
        $('.fa-bars').addClass('fa-times');
        $('.fa-times').removeClass('fa-bars');
    } else {
        $('#header').css({ 'right': '-16em' });
        $('#main').css({ 'transform': 'translateX(0em)' });
        $('.fa-times').addClass('fa-bars');
        $('.fa-bars').removeClass('fa-times');
    }
});

$('.hover').bind('touchstart touchend', function(e) {
    e.preventDefault();
    $(this).toggleClass('hover_mobile');
});